import React from 'react'
// import {
//   CheckboxContainer,
//   Icon,
//   HiddenCheckbox,
//   StyledCheckbox
// } from './styles';

const CheckboxContainer = styled.div` display: inline-block; vertical-align: middle; `;

const StyledChecbBox = styled.div` display: inline-block; width: 2rem; height: 2rem; border: ${props => props.checked ? 'none' : 'solid 0.1rem #dddddd'}; background: ${props => props.checked ? 'black' : 'white'}; border-radius: 0.4rem; transition: all 150ms; ${Icon} { visibility: ${props=>props.checked? 'visible': 'hidden'}; } `;


const Checkbox = ({ className, checked, ...props }) => (
  <CheckboxContainer className={className}>
    <HiddenCheckbox checked={checked} {...props} />
    <StyledCheckbox checked={checked}>
      <Icon viewBox="0 0 24 24">
        <polyline points="20 6 9 17 4 12" />
      </Icon>
    </StyledCheckbox>
  </CheckboxContainer>
)

export default Checkbox
