export async function createToken(emailid, password) {
  const response = await fetch("", {
    method: "post",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify({
      emailid: emailid,
      password: password,
    }),
  });
  return await response.json();
}
