import React from 'react'
import styled from 'styled-components';

// import {
//   CheckboxContainer,
//   Icon,
//   HiddenCheckbox,
//   StyledCheckbox
// } from './styles';

const CheckboxContainer = styled.div` display: inline-block; vertical-align: middle; `;

const StyledCheckbox = styled.div` display: inline-block; width: 2rem; height: 2rem; border: ${props => props.checked ? 'none' : 'solid 0.1rem #dddddd'}; background: ${props => props.checked ? 'black' : 'white'}; border-radius: 0.4rem; transition: all 150ms; ${Icon} { visibility: ${props=>props.checked? 'visible': 'hidden'}; } `;
const Icon = styled.svg` fill: none; stroke: white; stroke-width: 2px; `;
const HiddenCheckbox = styled.input` border: 0; clip: rect(0 0 0 0); clippath: inset(50%); height: 1px; margin: -1px; overflow: hidden; padding: 0; position: absolute; white-space: nowrap; width: 1px; `;


const Checkbox = ({ className, checked, ...props }) => (
  <CheckboxContainer className={className}>
    <HiddenCheckbox checked={checked} {...props} />
    <StyledCheckbox checked={checked}>
      <Icon viewBox="0 0 24 24">
        <polyline points="20 6 9 17 4 12" />
      </Icon>
    </StyledCheckbox>
  </CheckboxContainer>
)

export default Checkbox
