import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import * as style from "./styles";

function SideMenu(props) {
  const navigator = useNavigate();
  const [side, setSide] = useState("profile");
  const btnClicked = (e) => {
    e.preventDefault();
    if (e.target.tagName !== "DIV") {
      let target = e.target;
      while (e.target.tagName !== "BUTTON") {
        target = target.parentNode;
      }
      setSide(target.value);
    }
  };
  return (
    <style.sideContainer>
      <style.btnContainer onClick={btnClicked}>
        <style.sideBtn
          onClick={() => navigator("/profile")}
          value="profile"
          current={side === "profile"}
        >
          <style.Span>프로필</style.Span>
        </style.sideBtn>
        <style.sideBtn
          onClick={() => navigator("/interview")}
          value="interview"
          current={side === "interview"}
        >
          <style.Span>면접</style.Span>
        </style.sideBtn>
        <style.sideBtn
          onClick={() => navigator("/upload")}
          value="upload"
          current={side === "upload"}
        >
          <style.Span>업로드</style.Span>
        </style.sideBtn>
      </style.btnContainer>
    </style.sideContainer>
  );
}

export default SideMenu;
