import styled from "styled-components";

export const sideContainer = styled.div`
  display: flex;
  width: 30%;
  min-width: 300px;
  background-color: #e1e5e3;
  flex-direction: column;
  align-items: center;
  padding-top: 20px;
`;

export const sideBtn = styled.button`
  width: 80%;
  color: black;
  font-size: 15px;
  font-weight: bold;
  &:hover {
    background: #3cb371;
    border-radius: 12px;
  }
`;
