import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import * as style from "./styles";

function SignUp() {
  const navigator = useNavigate();
  const [values, setValues] = useState({
    email: "",
    password: "",
    repassword: "",
    name: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setValues({ ...values, [name]: value });
  };

  return (
    <style.Container>
      <style.Title>Create an account</style.Title>
      <style.SignUpForm>
        <style.Span
          color="#7a7a7a"
          size="14px"
          margins="0px 0px 8px 0px"
          weight="bold"
          font="AppleSD"
        >
          Full legal name
        </style.Span>
        <style.TextInput
          placeholder="Name"
          name="name"
          value={values.name}
          onChange={handleChange}
          required
          autoFocus
          InputProps={{ disableUnderline: true }}
        />
        <style.Span
          color="#7a7a7a"
          size="14px"
          margins="20px 0px 8px 0px"
          weight="bold"
          font="AppleSD"
        >
          Email address
        </style.Span>
        <style.TextInput
          placeholder="Email"
          name="email"
          value={values.email}
          onChange={handleChange}
          required
          autoFocus
          InputProps={{ disableUnderline: true }}
        />
        <style.Span
          color="#7a7a7a"
          size="14px"
          margins="20px 0px 8px 0px"
          weight="bold"
          font="AppleSD"
        >
          Password
        </style.Span>
        <style.TextInput
          required
          placeholder="Password"
          name="password"
          type="password"
          value={values.password}
          onChange={handleChange}
          error={values.password.length > 7 ? false : true}
          helperText={
            values.password.length === 0 || values.password.length > 7
              ? ""
              : "비밀번호는 8자리 이상 적어주세요"
          }
          InputProps={{ disableUnderline: true }}
        />
        <style.Span
          color="#7a7a7a"
          size="14px"
          margins="20px 0px 8px 0px"
          weight="bold"
          font="AppleSD"
        >
          Confirm Password
        </style.Span>
        <style.TextInput
          required
          placeholder="Confirm Password"
          name="repassword"
          type="password"
          value={values.repassword}
          onChange={handleChange}
          error={values.password === values.repassword ? false : true}
          helperText={
            values.password === values.repassword ||
            values.repassword.length === 0
              ? ""
              : "비밀번호가 서로 다릅니다."
          }
          InputProps={{ disableUnderline: true }}
        />
        <style.CheckForm>
          <style.IdCheckBox type="checkbox" />
          <style.Span size="14px" color="#929292" weight="bold" font="AppleSD">
            I have read the
          </style.Span>
          <style.TextBtn>Privacy Acknowledgement.</style.TextBtn>
        </style.CheckForm>
        <style.Button type="submit">Create an account</style.Button>
      </style.SignUpForm>
    </style.Container>
  );
}

export default SignUp;
