import React, { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import * as style from "./styles";

function SignUp() {
  const navigator = useNavigate();
  const [values, setValues] = useState({
    email: "",
    password: "",
    repassword: "",
    name: "",
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setValues({ ...values, [name]: value });
  };

  return (
    <style.Container>
      <style.Title>Create an account</style.Title>
      <style.SignUpForm>
        <style.Span>Full legal name</style.Span>
        <style.TextInput
          placeholder="Name"
          name="name"
          value={values.name}
          onChange={handleChange}
          required
          autoFocus
          InputProps={{ disableUnderline: true }}
        />
        <style.Span>Email address</style.Span>
        <style.TextInput
          placeholder="Email"
          name="email"
          value={values.email}
          onChange={handleChange}
          required
          autoFocus
          InputProps={{ disableUnderline: true }}
        />
        <style.Span>Password</style.Span>
        <style.TextInput
          required
          placeholder="Password"
          name="password"
          type="password"
          value={values.password}
          onChange={handleChange}
          error={values.password.length > 7 ? false : true}
          helperText={
            values.password.length > 7 ? "" : "비밀번호는 8자리 이상 적어주세요"
          }
          InputProps={{ disableUnderline: true }}
        />
        <style.Span>Confirm Password</style.Span>
        <style.TextInput
          required
          placeholder="Confirm Password"
          name="repassword"
          type="password"
          value={values.repassword}
          onChange={handleChange}
          error={values.password === values.repassword ? false : true}
          helperText={
            values.password === values.repassword
              ? ""
              : "비밀번호가 서로 다릅니다."
          }
          InputProps={{ disableUnderline: true }}
        />

        <style.Button type="submit">Create an account</style.Button>
      </style.SignUpForm>
    </style.Container>
  );
}

export default SignUp;
