import React from 'react';
import * as api from '../../api/server';
import './styles.css';
import Checkbox from '../../components/Checkbox/Checkbox';
import {
  NavBtn,
  LoginBtnLink,
  Wrap
  } from '../../components/Navbar/NavbarElements';



class LoginForm extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      fields: {},
      errors: {}
    }

    this.handleChange = this.handleChange.bind(this);
    this.submituserRegistrationForm = this.submituserRegistrationForm.bind(this);

  };

  handleChange = event =>  {
    let fields = this.state.fields;
    fields[event.target.name] = event.target.value;
    this.setState({
      fields
    });

  }

  submituserRegistrationForm = async(event) => {
    // event.preventDefault();
    // if (this.validateForm()) {
    //     let fields = {};
    //     fields["username"] = this.state.fields.username;
    //     fields["emailid"] = this.state.fields.emailid;
    //     fields["password"] = this.state.fields.password;
    //     this.setState({fields:fields});

    //     const { history } = this.props;
    //     const token = await api.createUser(fields);
    //     if(token.non_field_errors){
    //         token.non_field_errors.map((event) => alert(event))
    //     } else{
    //         history.push('/');
    //     }

    //     alert("Form submitted");
    // }

  }




  render() {
    return (
    <div>
     <div id="register">
        <h3>Login</h3>
          <form method="post"  name="userRegistrationForm"  onSubmit= {this.submituserRegistrationForm} >

          <label>Email address</label>
          <input type="text" name="emailid" placeholder="Enter your email address" onChange={(e) => setInputData('username', e.target.value)}/>
          <div className="errorMsg">{this.state.errors.emailid}</div>

          <label>Password</label>
          <input type="password" name="password" placeholder="Enter your password" value={this.state.fields.password} onChange={this.handleChange} />
          <div className="errorMsg">{this.state.errors.password}</div>


          <NavBtn>
            <div>
              <Checkbox
                checked={this.state.checked}
                onChange={this.handleCheckboxChange}
              />
              <span style={{ marginLeft: 8 }}>Remember me</span>
            </div>
            <span style={{ color: '#3CB371'}}>Reset Password?</span>
        </NavBtn>

        <Wrap>
          <NavBtn>
            <LoginBtnLink type="submit" to='/signup'>Create an account</LoginBtnLink>
            <input type="submit" className="button"  value="Login"/>
          </NavBtn>
        </Wrap>
        </form>
    </div>
</div>

      );
  }


}


export default LoginForm;
