import styled from "styled-components";

export const sideContainer = styled.div`
  display: flex;
  width: 30%;
  min-width: 300px;
  height: 100%;
  min-height: 1020px;
  background-color: #e1e5e3;
  flex-direction: column;
  align-items: center;
  padding-top: 20px;
`;

export const sideBtn = styled.button`
  width: 80%;
  height: 30px;
  color: black;
  font-size: 13px;
  font-weight: bold;
  margin-bottom: 10px;
  background-color: transparent;
  border: none;
  text-align: left;
  &:hover {
    cursor: pointer;
    background: #3cb371;
    border-radius: 12px;
    color: white;
  }
`;

export const Span = styled.p`
  margin: 0.3rem 1rem;
`;
